import React, { Component } from "react";
import Menu from "../../menu/menu";
import Select from 'react-select';
import api from "../../../services/api";
import Groups from "../../../utils/groups.json";
import { Form, Container } from "./styles";

export default class activateExts extends Component {
    constructor() {
        super();
        this.state = {
            page_num: 1,
            extSelected: null,
            extGroupSelected: null,
            selectedOption: null,
            ext: null,
            group: null,
            info: [],
            status: 'P',
            data_validacao_cliente: null,
            toChange: 'Y',
        }
    }

    componentDidMount = () => {
        this.pendentExt(this.state.status);
        this.pendentGroup();

    }

    // list all exts per status
    pendentExt = async (status) => {
        const { page_num } = this.state;
        let exts = [];
        const response = await api.get(`/extensions/${page_num}/${status}`);
        const { docs } = response.data;
        console.log(docs);
        exts = docs.map((dt) => { return { value: dt.linha, label: dt.linha } })
        this.setState({ info: docs, ext: exts });
    }

    // select groups from json to fill list of groups
    pendentGroup = () => {
        let groups = [];
        groups = Groups.groups.map((dt) => { return { value: dt.value, label: dt.label } })
        this.setState({ group: groups })
    }

    // List ext selected
    extChange = async (extSelected) => {
        const { page_num, status } = this.state;
        let selectedGroup;
        const response = await api.get(`/extensions/${page_num}/${status}`);
        const { docs } = response.data;
        selectedGroup = docs.map((dt) => { return { value: dt.user_group_id, label: dt.Grupo, ext: dt.linha } }).find((sg) => sg.ext === extSelected.value)
        this.setState({ extSelected, extGroupSelected: selectedGroup });

    };

    // select group
    extGroupChange = extGroupSelected => {
        this.setState({ extGroupSelected });
        console.log(`Grupo Selected: `, extGroupSelected);
    }

    // handle action on radio button
    handleOptionChange = async changeEvent => {
        let status = null;
        let event = null;
        let toChange = null;

        if (changeEvent.target.id === 'activateExt') {
            status = 'P';
            toChange = 'Y'
            event = changeEvent.target.value;
        }
        if (changeEvent.target.id === 'changeExt') {
            status = 'Y';
            event = changeEvent.target.value;
        }
        if (changeEvent.target.id === 'deactivateExt') {
            status = 'Y';
            toChange = 'N'
            event = changeEvent.target.value;
        }

        this.setState({ selectedOption: event, status, toChange });
        this.pendentExt(status);
    }

    handleSubmit = async (e) => {
        e.preventDefault();
        const { extSelected, extGroupSelected, toChange, data_validacao_cliente } = this.state;
        // const group = extGroupSelected.value;
        console.log("Result", this.state);
        if (!extSelected || !data_validacao_cliente || !toChange) {
            alert("Preencha todos os dados!");
        } else {
            const ext = extSelected.value ;
            const status = toChange;
            try {
                const post = await api.post(`/extensions/updtext/${ext}`, { status, data_validacao_cliente });
                alert(`Ramal ${ext} ativado com sucesso!`);
                this.pendentExt(this.state.status);
            } catch (err) {
                console.log(err);
                alert("Ocorreu um erro ao ativar o ramal");
            }
        }
    }

    render() {
        const { extSelected, ext, extGroupSelected, group, selectedOption } = this.state;
        return (
            <div className="mainDiv">
                <div className="menu">
                    <Menu />
                </div>

                <div className="formKeepExt">
                    <p>Manter Ramais</p>
                    <Container>
                        <Form onSubmit={this.handleSubmit}>
                            <Select
                                className="selected"
                                value={extSelected}
                                onChange={this.extChange}
                                options={ext}
                                maxMenuHeight={150}
                                placeholder="Selecione o Ramal"
                                isSearchable />
                            <Select
                                className="selected"
                                value={extGroupSelected}
                                onChange={this.extGroupChange}
                                options={group}
                                maxMenuHeight={150}
                                placeholder="Selecione o Grupo"
                                isSearchable />

                            <div className="radio">
                                <input
                                    id="activateExt"
                                    type="radio"
                                    className="radio"
                                    name="radioActive"
                                    value="activate"
                                    checked={selectedOption === "activate"}
                                    onChange={this.handleOptionChange} />
                                <label for="activateExt">Ativar Ramal</label>
                                <input
                                    id="changeExt"
                                    name="radioChange"
                                    className="radio"
                                    type="radio"
                                    value="changeExt"
                                    checked={selectedOption === "changeExt"}
                                    onChange={this.handleOptionChange} />
                                <label for="changeExt"> Mudar Ramal de Grupo</label>
                                <input
                                    id="deactivateExt"
                                    type="radio"
                                    className="radio"
                                    name="radioDeactivate"
                                    value="deactivateExt"
                                    checked={selectedOption === "deactivateExt"}
                                    onChange={this.handleOptionChange} />
                                <label for="deactivateExt">Desconectar</label>
                            </div>
                            <a>Data de Ativação</a>
                            <input className="dtActivate"
                                type="date"
                                placeholder="Data de Ativação"
                                onChange={e => this.setState({ data_validacao_cliente: e.target.value })}
                                required
                            />
                            <button type="submit">Executar</button>
                        </Form>
                    </Container>
                </div>
            </div >

        )
    }
}
