import React, { Component } from 'react'
import { Dropdown, Icon, Input, Menu } from 'semantic-ui-react'

const styleLink = document.createElement("link");
styleLink.rel = "stylesheet";
styleLink.href = "https://cdn.jsdelivr.net/npm/semantic-ui/dist/semantic.min.css";
document.head.appendChild(styleLink);

export default class MenuExampleSubMenu extends Component {
  state = {}

  handleItemClick = (e, { name }) => this.setState({ activeItem: name })

  render() {
    const { activeItem } = this.state

    return (
      <Menu vertical>
        <Menu.Item>
          <Input placeholder='Search...' />
        </Menu.Item>

        <Menu.Item>
          Home
          <Menu.Menu>
            <Menu.Item
              name='faturamento'
              active={activeItem === 'faturamento'}
              onClick={this.handleItemClick}
            >
              Gerar Faturamento
            </Menu.Item>
            <Menu.Item
              name='visualizarFaturamento'
              active={activeItem === 'visualizarFaturamento'}
              onClick={this.handleItemClick}
            >
              Visualizar Faturamentos
            </Menu.Item>
            <Menu.Item
              name='consultarFaturamentos'
              active={activeItem === 'consultarFaturamentos'}
              onClick={this.handleItemClick}
            >
              Faturamentos Realizados
            </Menu.Item>
          </Menu.Menu>
        </Menu.Item>

        <Menu.Item
          name='browse'
          active={activeItem === 'browse'}
          onClick={this.handleItemClick}
        >
          <Icon name='grid layout' />
          Browse
        </Menu.Item>
        <Menu.Item
          name='messages'
          active={activeItem === 'messages'}
          onClick={this.handleItemClick}
        >
          Messages
        </Menu.Item>

        <Dropdown item text='Faturamento'>
          <Dropdown.Menu>
            <Dropdown.Item icon='edit' text='Edit Profile' />
            <Dropdown.Item icon='globe' text='Choose Language' />
            <Dropdown.Item 
                icon='settings' 
                text='Account Settings'
                name='account'
                active={activeItem === 'account'}
                onClick={this.handleItemClick}/>
          </Dropdown.Menu>
        </Dropdown>
      </Menu>
    )
  }
}
