import React, { Component } from "react";
import Menu from "../../menu/menu";
import Select from 'react-select';
import api from "../../../services/api";

export default class activateExts extends Component {
    constructor(){
        super();
        this.state={
            page_num:1,
            extSelected: null,
            ext: null,
            info: [],
        }
    }

    componentDidMount = () =>{
        this.pendent();
    }

    pendent = async () => {
        const { page_num } = this.state;
        let exts = [];
        const response = await api.get(`/extensions/${page_num}/Y`);
        const { docs } = response.data;
        // console.log(docs)
        exts = docs.map((dt) => { return { value: dt.linha, label: dt.linha } })
        console.log(exts)
        this.setState({ info: docs, ext: exts });
    }

    periodChange = extSelected => {
        this.setState({ extSelected });
        console.log(`Período Selected: `, extSelected.value);
    };

    render(){
        const { extSelected, ext} = this.state;
        return (
            <div className="mainDiv">
                <div className="menu">
                    <Menu />
                </div>
                <p>Ativar Ramais</p>
                <div className="doBackup">
                        <div className="finders">
                            <Select
                                className="selected"
                                value={extSelected}
                                onChange={this.periodChange}
                                options={ext}
                                placeholder="Selecione o Período"
                                isSearchable />
                            <button className="doBackup" onClick={this.handleSubmit}>Pesquisar</button>
                        </div>
                    </div>
                
            </div>

        )
    }
}
