import React, { Component } from "react";
import Menu from "../../menu/menu";
import Select from 'react-select';
import api from "../../../services/api";
import "./styles.css";

const year = [
    { value: 0, label: 2015 },
    { value: 1, label: 2016 },
    { value: 2, label: 2017 },
    { value: 3, label: 2018 },
    { value: 4, label: 2019 },
    { value: 5, label: 2020 },
    { value: 6, label: 2021 },
    { value: 7, label: 2022 },
    { value: 8, label: 2023 },
    { value: 9, label: 2024 },
    { value: 10, label: 2025 },
    { value: 11, label: 2026 },
    { value: 12, label: 2027 },
    { value: 13, label: 2028 },
    { value: 14, label: 2029 },
    { value: 15, label: 2030 },
];
const month = [
    { value: 1, label: 'Janeiro' },
    { value: 2, label: 'Fevereiro' },
    { value: 3, label: 'Março' },
    { value: 4, label: 'Abril' },
    { value: 5, label: 'Maio' },
    { value: 6, label: 'Junho' },
    { value: 7, label: 'Julho' },
    { value: 8, label: 'Agosto' },
    { value: 9, label: 'Setembro' },
    { value: 10, label: 'Outubro' },
    { value: 11, label: 'Novembro' },
    { value: 12, label: 'Dezembro' }];

export default class MainFunction extends Component {
    constructor() {
        super();
        this.state = {
            selectedYear: [],
            selectedMonth: null
        }
    }

    handleSubmit = async e => {
        const { selectedYear, selectedMonth } = this.state;
        const response = await api.get(`/pmpg/${selectedYear.value}${selectedMonth.value}`);
        console.log(response)

    }

    async componentDidMount() {
        let year = []
        const { selectedYear } = this.state;
        const response = await api.get(`/ano`);
        year = response.data.map((selectedYear) => { return selectedYear})
        console.log("Year", year);
        this.setState({ selectedYear: response.data })

    }


    yearChange = async e => {
        const { selectedYear } = this.state;
        // const response = await api.get(`/ano`)
        // let ano = null
        console.log("Selected_year", selectedYear)
        let year = selectedYear.map((ano) => {return ano})
        console.log(year)
        // ano = response.data.map((selectedYear) => { return selectedYear})
        // console.log(`Year Selected: `, ano);
        // this.setState({ selectedYear: response });
    };
    monthChange = selectedMonth => {
        this.setState({ selectedMonth });
        console.log(`Month Selected: `, selectedMonth.value);
    };

    render() {
        const { selectedYear, selectedMonth } = this.state;
        console.log("Ano", selectedYear);
        return (
            <div className="main">
                <div className="menu">
                    <Menu />
                </div>
                <h1>Executar Faturamento</h1>
                <div className="finders">
                    <Select
                        className="selectedYear"
                        value={selectedYear}
                        onChange={this.yearChange}
                        options={year}
                        placeholder="Selecione o Ano"
                        isSearchable />

                    <Select
                        className="selectedMonth"
                        value={selectedMonth}
                        onChange={this.monthChange}
                        options={month}
                        placeholder="Selecione o Mês"
                        isSearchable />
                    <button className="findButton" onClick={this.handleSubmit}>Executar Faturamento</button>
                </div>

            </div>

        )
    }
}
