import React, { Component } from "react";
import Menu from "../../menu/menu";

export default class showBilling extends Component {


    render(){
        const { selectedYear, selectedMonth, year, month } = this.state;
        return (
            <div className="main">
                <div className="menu">
                    <Menu />
                </div>
                <h1>Executar Faturamento</h1>
                <div className="finders">
                    <Select
                        className="selected"
                        value={selectedYear}
                        onChange={this.yearChange}
                        options={year}
                        placeholder="Selecione o Ano"
                        isSearchable />

                    <Select
                        className="selected"
                        value={selectedMonth}
                        onChange={this.monthChange}
                        options={month}
                        placeholder="Selecione o MÃªs"
                        isSearchable />
                    <button className="findButton" onClick={this.handleSubmit}>Executar Faturamento</button>
                </div>

            </div>

        )
    }
}
