import React, { Component } from "react";
import Menu from "../../menu/menu";
import Select from 'react-select';
import Data from "../../../services/api";
import Currency from 'react-currency-format';


export default class showConsumeTotal extends Component {
    constructor() {
        super();
        this.state = {
            year: null,
            month: null,
            selectedYear: new Date().getFullYear(),
            selectedMonth: parseInt(new Date().getMonth()) + 1,
            total: 0,
            total_ext: 0,
            total_llm: 0,
            total_exc: 0,
            total_min: 0,
            content: [],
        }
    }
    componentDidMount() {
        this.loadYearMonth();
        // this.total();
    }

    loadYearMonth = async () => {
        let ano = [];
        let mes = [];
        // const { selectedYear, year } = this.state;
        const resp_Y = await Data.get(`/ano`);
        const resp_M = await Data.get(`/mes`);
        ano = resp_Y.data.map((id_ano) => { return { value: id_ano.id_ano, label: id_ano.id_ano } });
        mes = resp_M.data.map((id_mes) => { return { value: id_mes.id_mes, label: id_mes.mes } });
        // console.log("Years", ano);
        this.setState({ year: ano, month: mes });
    }
    // Year Selected
    yearChange = selectedYear => {
        this.setState({ selectedYear });
        // console.log(`Year Selected: `, selectedYear.value);
    };
    // Month Selected
    monthChange = selectedMonth => {
        this.setState({ selectedMonth });
        // console.log(`Month Selected: `, selectedMonth.value);
    };

    handleSubmit = async e => {
        const { selectedYear, selectedMonth } = this.state;

        const response = await Data.get(`/pmpg/total/${selectedYear.value}${selectedMonth.value}`);
        const { data } = response;
        console.log("Data", data)
        this.setState({ content: data });
        console.log(this.state.content);
        this.total()
    }

    minutes = (time) => {
        const part = (String(time === 0 ? '0.0' : time).split('.'))
        const min = isNaN(part[0]) ? 0 : parseInt(part[0]);
        const sec = isNaN(part[1]) ? 0 : parseInt(part[1]);
        // console.log("part", part, time);
        // console.log("part[0]", parseInt(part[0]))
        console.log("total_minutes", (parseInt(min)))
        console.log("total_seconds", Math.ceil(parseInt(sec)))
        console.log("total", (parseInt(min) * 60) + Math.ceil(parseInt(sec) / 60))
        // const total_minutes = parseInt(min) + Math.round(parseInt(sec) / 60 , 0)
        // ((parseInt(min) * 60) + sec) / 60 
        // ((parseInt(min) * 60) + sec ) * 0.016667
        //0.016666478
        const total_min = ((((parseInt(min) * 60))) + sec ) / 60
        const total_time = Math.round(total_min,0)

        const total_seconds = sec / 60 
        // ((parseInt(min)) === 0 ? 0 : (parseInt(min))) + ((parseInt(sec) % 60)) === 0 ? 0 : ((parseInt(sec) % 60))
        return total_seconds
    }

    mask = (Value, Decimal, Prefix, TS) => {
        const valor = (<Currency displayType={'text'}
            value={Value}
            thousandSeparator={TS}
            decimalSeparator={Decimal}
            prefix={Prefix}
            decimalScale={2}
            fixedDecimalScale={true} />)
        return valor
    }

    total = () => {
        const { content } = this.state;
        const total_geral = (content.map((ttl) => (ttl.total_faturar)).reduce((tt, next) => (tt + next)))
        const total_ext = (content.map((ttl) => (ttl.total)).reduce((tt, next) => (tt + next)))
        const total_llm = (content.map((ttl) => (ttl.local_valor
            + ttl.ldn_valor
            + ttl.movel_valor)).reduce((tt, next) => (tt + next)))
        const total_exc = (content.map((ttl) => (ttl.local_exc
            + ttl.ldn_exc
            + ttl.movel_exc))).reduce((tt, next) => (tt + next))
        const total_min = (content.map((ttl) => (this.minutes(ttl.movel_exc_m)
            //  ttl.local_exc_m
            // + ttl.ldn_exc_m
            // + ttl.movel_exc_m)
        ))).reduce((tt, next) => (tt + next))
        this.setState({
            total: total_geral
            , total_ext: total_ext
            , total_llm: total_llm
            , total_exc: total_exc
            , total_min: total_min
        })
    }

    render() {
        const { selectedYear, selectedMonth, year, month, total, total_ext, total_llm, total_exc, total_min } = this.state;
        return (
            <div className="mainDiv">
                <div className="menu">
                    <Menu />
                </div>
                <h1>Pesquisar Faturamento - Consumo Total</h1>
                <div className="finders">
                    <Select
                        className="selected"
                        value={selectedYear}
                        onChange={this.yearChange}
                        options={year}
                        placeholder="Selecione o Ano"
                        isSearchable />

                    <Select
                        className="selected"
                        value={selectedMonth}
                        onChange={this.monthChange}
                        options={month}
                        placeholder="Selecione o Mês"
                        isSearchable />
                    <button className="findButton" onClick={this.handleSubmit}>Pesquisar</button>
                </div>
                <div>
                    <div className="total">
                        <h1>Total Geral</h1>
                        <p>Total Serviços:{this.mask(total, ",", "R$ ", ".")}</p>
                        <p className="ttl">Total Ramais: {this.mask(total_ext, ",", "R$ ", ".")}</p>
                        <p className="ttl">Total Franquias: {this.mask(total_llm, ",", "R$ ", ".")}</p>
                        <p className="ttl">Total Excedentes: {this.mask(total_exc, ",", "R$ ", ".")}</p>
                        <p className="ttl">Total Minutos: {this.mask(total_min, ":", " ")}</p>
                    </div>
                </div>
            </div>

        )
    }
}
