/* eslint-disable jsx-a11y/anchor-is-valid */
import React, { Component } from "react";
import Menu from "../../menu/menu";
import { Form, Container } from "./styles";
import Select from 'react-select';
// import api from "../../../services/api";

const groups = [
    { value: 2824, label: "Prefeitura de Ponta Grossa - PMPG" },
    { value: 2831, label: "Prefeitura de Ponta Grossa - SME - ESCOLA" },
    { value: 2832, label: "Prefeitura de Ponta Grossa - SME - CMEI" },
    { value: 2833, label: "Prefeitura de Ponta Grossa - SMS - PAB" },
    { value: 2834, label: "Prefeitura de Ponta Grossa - SMS - FAE" },
    { value: 2835, label: "Prefeitura de Ponta Grossa - SMS - AIH" },
    { value: 2855, label: "Prefeitura de Ponta Grossa - SAMU" },
    { value: 2857, label: "Prefeitura de Ponta Grossa - BOMBEIROS" }
]

export default class KeepExts extends Component {
    constructor(){
        super();
        this.state ={
            linha: null,
            user_group_id: null,
            tipo_linha: null,
            data_envio_nova: null,
            data_alteracao: null,
            status: null ,
            groups,
            groupSelected: null
        };
    }
    componentWillMount = () => {

    }

    handleSubmit = async (e) => {
        e.preventDefault();
        const { linha, user_group_id, tipo_linha, data_envio_nova, status, groupSelected } = this.state;
        if ( !linha || !user_group_id || !tipo_linha || data_envio_nova || status || groupSelected ){
            alert("Preencha todos os dados!");
        } else {
            try {
                await api.post("/extensions/regext", { linha, user_group_id, tipo_linha, data_envio_nova, status, groupSelected });
                this.props.history.push("/app/ramais");
            } catch (err) {
                console.log(err);
                alert("Ocorreu um erro ao registrar o ramal");
            }
        }
    }

    groupChange = groupSelected => {
        this.setState({ groupSelected });
        console.log(`Group Selected: `, groupSelected);
    };

    // onChange = (e) => {
    //     let val = e.target.value.replace(/[^\x00-\x7F]/ig, '');
    //     this.setState({ status: val });
    // }

    render(){
        const { groups, groupSelected } = this.state;
        return (
            <div className="mainDiv">
                <div className="menu">
                    <Menu />
                </div>
                <p>Manter Ramais</p>
                <div className="formExt">
                    <Container>
                        
                        <Form onSubmit={this.handleSubmit}>
                        <a>Ramal ou Linha</a>
                        <input 
                            type="number"
                            placeholder="Ramal ou Linha"
                            onChange={ e => this.setState({ linha: e.target.value })}
                            />
                        <a>Grupos</a>
                        <Select
                                className="selected"
                                value={groupSelected}
                                onChange={this.groupChange}
                                options={groups}
                                maxMenuHeight={150}
                                placeholder="Selecione o Grupo"
                                isSearchable />
                        <a>Tipo de Linha</a>
                        <input 
                            type="text"
                            placeholder="Tipo de Linha"
                            onChange={ e => this.setState({ tipo_linha: e.target.value })}
                            />
                        <a>Data de Cadastro</a>
                        <input 
                            type="date"
                            placeholder="Data de Cadastro"
                            onChange={ e => this.setState({ data_envio_nova: e.target.value })}
                            />
                        <a>Data de Alteração</a>
                        <input 
                            type="date"
                            placeholder="Data de Alteração"
                            onChange={ e => this.setState({ data_alteracao: e.target.value })}
                        />
                        <a>Status</a>
                        <input 
                            type="Text"
                            placeholder="Status"
                            onKeyPress={event => (event.charCode >= 65 && event.charCode <= 90) || (event.charCode >= 97 && event.charCode <= 122)}
                            maxlength="1"
                            onChange={ e => this.setState({ status: e.target.value })}
                            />
                        <button type="submit">Registrar</button>
                        </Form>
                    </Container>

                </div>
            </div>

        )
    }
}
