import React, { Component } from "react";
import Menu from "../../menu/menu";
import Select from 'react-select';
import api from "../../../services/api";
import Groups from "../../../utils/groups.json";

export default class activateExts extends Component {
    constructor(){
        super();
        this.state={
            page_num:1,
            extSelected: null,
            extGroupSelected: null,
            ext: null,
            group: null,
            info: [],
        }
    }

    componentDidMount = () =>{
        this.pendentExt();
        this.pendentGroup();
    }

    
    // Retorna os ramais pendentes para ativação
    pendentExt = async () => {
        const { page_num } = this.state;
        let exts = [];
        let selectedGroup;
        const response = await api.get(`/extensions/${page_num}/P`);
        const { docs } = response.data;
        exts = docs.map((dt) => { return { value: dt.linha, label: dt.linha } })
        
        console.log(selectedGroup);
        this.setState({ info: docs, ext: exts});
        this.extGroupChange(selectedGroup)
    }

    pendentGroup = ( ) => {
        let groups = [];
        groups = Groups.groups.map((dt) => {return {value: dt.value, label:dt.label}})
        this.setState({group: groups})
    }

    extChange = async extSelected => {
        const response = await api.get(`/extensions/${page_num}/P`);
        selectedGroup = docs.map((dt) => {return { value: dt.user_group_id, label: dt.Grupo}})
        this.setState({ extSelected, extGroupSelected: selectedGroup });
        console.log(`Período Selected: `, extSelected.value);
    };
    
    extGroupChange = extGroupSelected => {
        this.setState({ extGroupSelected });
        console.log(`Grupo Selected: `, extGroupSelected);
    }
    

    render(){
        const { extSelected, ext, extGroupSelected, group} = this.state;
        return (
            <div className="mainDiv">
                <div className="menu">
                    <Menu />
                </div>
                <p>Ativar Ramais</p>
                <div className="doBackup">
                        <div className="finders">
                            <Select
                                className="selected"
                                value={extSelected}
                                onChange={this.extChange}
                                options={ext}
                                maxMenuHeight={150}
                                placeholder="Selecione o Ramal"
                                isSearchable />
                            <Select
                                className="selected"
                                value={extGroupSelected}
                                onChange={this.extGroupChange}
                                options={group}
                                maxMenuHeight={150}
                                placeholder="Selecione o Grupo"
                                isSearchable />    
                            <button className="doBackup" onClick={this.handleSubmit}>Executar</button>
                        </div>
                    </div>
                
            </div>

        )
    }
}
