import React, { Component } from "react";
import Menu from "../../menu/menu";
import Select from 'react-select';
import api from "../../../services/api";
import "./styles.css";

const year = [
    { value: 2015, label: 2015 },
    { value: 2016, label: 2016 },
    { value: 2017, label: 2017 },
    { value: 2018, label: 2018 },
    { value: 2019, label: 2019 },
    { value: 2020, label: 2020 },
    { value: 2021, label: 2021 },
    { value: 2022, label: 2022 },
    { value: 2023, label: 2023 },
    { value: 2024, label: 2024 },
    { value: 2025, label: 2025 },
    { value: 2026, label: 2026 },
    { value: 2027, label: 2027 },
    { value: 2028, label: 2028 },
    { value: 2029, label: 2029 },
    { value: 2030, label: 2030 },
];
const month = [
    { value: 1, label: 'Janeiro' },
    { value: 2, label: 'Fevereiro' },
    { value: 3, label: 'Março' },
    { value: 4, label: 'Abril' },
    { value: 5, label: 'Maio' },
    { value: 6, label: 'Junho' },
    { value: 7, label: 'Julho' },
    { value: 8, label: 'Agosto' },
    { value: 9, label: 'Setembro' },
    { value: 10, label: 'Outubro' },
    { value: 11, label: 'Novembro' },
    { value: 12, label: 'Dezembro' }];

export default class MainFunction extends Component {

    state = {
        selectedYear: null,
        selectedMonth: null
    }

    handleSubmit = async e => {
        const { selectedYear, selectedMonth } = this.state;
        const response = await api.get(`/pmpg/${selectedYear.value}${selectedMonth.value}`);
        console.log(response)

    }

    yearChange = async e => {
        const { selectedYear } = this.state;
        const response = await api.get(`/ano/${selectedYear.value}`)
        console.log(`Year Selected: `, selectedYear.value, response);
        // this.setState({ selectedYear: response });
    };
    monthChange = selectedMonth => {
        this.setState({ selectedMonth });
        console.log(`Month Selected: `, selectedMonth.value);
    };

    render() {
        const { selectedYear, selectedMonth } = this.state;
        return (
            <div className="main">
                <div className="menu">
                    <Menu />
                </div>
                <h1>Executar Faturamento</h1>
                <div className="finders">
                    <Select
                        className="selectedYear"
                        value={selectedYear}
                        onChange={this.yearChange}
                        options={year}
                        placeholder="Selecione o Ano"
                        isSearchable />
                        
                    <Select
                        className="selectedMonth"
                        value={selectedMonth}
                        onChange={this.monthChange}
                        options={month}
                        placeholder="Selecione o Mês"
                        isSearchable />
                    <button className="findButton" onClick={this.handleSubmit}>Executar Faturamento</button>
                </div>

            </div>

        )
    }
}
