import styled from "styled-components";

export const Container = styled.div`
    display: flex;
    align-itens: center;
    justify-content: center;
    height: 90vh;
`;

export const Form = styled.form`
  width: 500px;
  background: #fff;
  padding: 30px;
  display: flex;
  flex-direction: column;
  align-items: justify;
  img {
    width: 200px;
    margin: 10px 0 40px;
  }
  p {
    color: #ff3333;
    margin-bottom: 15px;
    border: 1px solid #ff3333;
    padding: 10px;
    width: 100%;
    text-align: center;
  }

  input {
    flex: 1;
    height: 26px;
    margin-bottom: 15px;
    padding: 0 20px;
    color: #777;
    font-size: 15px;
    width: 100%;
    border: 1px solid #ddd;
    &::placeholder {
      color: #999;
    }
  }
  
  .radio {
    margin: 10px;
    padding: 10px;
    justify-content: space-between;
  }
  .radio input[type="radio"] {
    opacity: 0;
    position: fixed;
    width: 10px;
  }
  .radio label {
    display: inline-block;
    background-color: #ddd;
    padding: 10px 20px;
    font-family: sans-serif, Arial;
    font-size: 14px;
    border: 2px solid #444;
    border-radius: 4px;
}
.radio label:hover {
  background-color: #dfd;
}
.radio input[type="radio"]:focus + label {
  border: 2px #444;
}
.radio input[type="radio"]:checked + label {
  background-color: #bfb;
  border-color: #4c4;
}

  button {
    color: #fff;
    font-size: 16px;
    background: #ff9b42;
    height: 56px;
    border: 0;
    border-radius: 5px;
    width: 100%;
    cursor: pointer;
  }
  hr {
    margin: 20px 0;
    border: none;
    border-bottom: 1px solid #cdcdcd;
    width: 100%;
  }
  a {
    text-align: left;
    font-size: 18px;
    font-weight: bold;
    color: #999;
    text-decoration: none;

    margin-top: 10px;
    margin-bottom: 5px;

  }
  .selected {
    width: 100%;
    font-weight: bold;
  }
`;
