import React, { Component } from "react";
import Menu from "../../menu/menu";


export default class SearchExtsMonth extends Component {
    constructor(){
        super();
        this.state={
            ext:0,
            page:1,
        }
    }

    componentDidMount = () => {

    }

    // List ext selected
    extChange = async (extSelected) => {
        const { ext, page } = this.state;
        const response = await api.get(`/extensions/${ext}/${page}`);
        console.log(response )
        this.setState({ extSelected, extGroupSelected: selectedGroup });
    };

    render() {
        const { extSelected, ext } = this.state;
        return (
            <div className="mainDiv">
                <div className="menu">
                    <Menu />
                </div>
                <h1>Consultar Ramais</h1>
                <div className="finders">
                    <Select
                        className="selected"
                        value={extSelected}
                        onChange={this.extChange}
                        options={ext}
                        placeholder="Selecione o Ramal"
                        maxMenuHeight={250}
                        isSearchable />
                    <button className="findButton" onClick={this.handleSubmit}>Pesquisar</button>
                </div>

                {this.showExts()}
            </div>
        )
    }
}
