/* eslint-disable jsx-a11y/anchor-is-valid */
import React, { Component } from "react";
import { withRouter } from "react-router-dom";
import Menu from "../../menu/menu";
import { Form, Container } from "./styles";
import Select from 'react-select';
import api from "../../../services/api";
import Groups from "../../../utils/groups.json";


class keepFares extends Component {
    constructor() {
        super();
        this.state = {
            linha: null,
            user_group_id: null,
            tipo_linha: null,
            data_envio_nova: null,
            data_alteracao: null,
            status: null,
            groups: null,
            group: null,
            groupSelected: null,
            info: [],
            data: null,
        };
    }
    componentDidMount = () => {
        // console.log("Data", this.state.data);
        this.history();
        this.pendentGroup();
    }

    handleSubmit = async (e) => {
        e.preventDefault();
        const { linha, groupSelected, tipo_linha, data_envio_nova, data_alteracao, status } = this.state;
        // console.log(`Validacao ${linha}, ${user_group_id.value}, ${tipo_linha}, ${data_envio_nova}, ${status}`)
        if (!linha || !groupSelected || !tipo_linha || !data_envio_nova || !data_alteracao || !status) {
            alert("Preencha todos os dados!");
        } else {
            let user_group_id = groupSelected.value;
            try {
                // const post = 
                await api.post("/extensions/regext", { linha, user_group_id, tipo_linha, data_envio_nova, data_alteracao, status });
                // const { data } = post;
                alert('Ramal Cadastrado com Sucesso!');
                this.props.history.push("/app/ramais");
                this.history();
                // this.setState( state => ({info: [ data, ...state.info ]}));

            } catch (err) {
                console.log(err);
                alert("Ocorreu um erro ao registrar o ramal");
            }
        }
    }

    history = async (e) => {
        const response = await api.get(`/extKeep/P`);
        const { data } = response;
        // console.log('History', data);
        this.setState({ info: data });
    }

    groupChange = groupSelected => {
        // const { user_group_id, ...data } = this.state;
        // console.log('Estado ', data.linha);
        this.setState({ groupSelected });
        console.log(`Group Selected: `, groupSelected);
    };

    pendentGroup = () => {
        let groups = [];
        groups = Groups.groups.map((dt) => { return { value: dt.value, label: dt.label } })
        this.setState({ groups: groups })
    }


    render() {
        const { groups, groupSelected, info } = this.state;
        // console.log("Info", info, groupSelected);

        return (
            <div className="mainDiv">
                <div className="menu">
                    <Menu />
                </div>
                <p>Manter Tarifas</p>
                <div className="formExt">
                    <Container>

                        <Form onSubmit={this.handleSubmit}>
                            <a>Grupos</a>
                            <Select
                                className="selected"
                                value={groupSelected}
                                onChange={this.groupChange}
                                options={groups}
                                maxMenuHeight={150}
                                placeholder="Selecione o Grupo"
                                isSearchable
                                required
                            />
                            <a>Tipo Tarifa</a>
                            <Select
                                className="selected"
                                value={groupSelected}
                                onChange={this.groupChange}
                                options={groups}
                                maxMenuHeight={150}
                                placeholder="Selecione o Grupo"
                                isSearchable
                                required
                            />
                            <a>Custo</a>
                            <input
                                type="number"
                                placeholder="Custo"
                                onChange={e => this.setState({ CUSTO: e.target.value })}
                                required
                            />
                            <a>Franquia Minutos</a>
                            <input
                                type="date"
                                placeholder="Franquia Minutos"
                                onChange={e => this.setState({ DATA_ALTERACAO: e.target.value })}
                                required
                            />
                            <a>Franquia Valor</a>
                            <input
                                type="text"
                                placeholder="Data de Alteração"
                                onChange={e => this.setState({ FRANQUIA_MIN: e.target.value })}
                                required
                            />
                            <a>Valor Ramal</a>
                            <input
                                type="money"
                                placeholder="Valor Ramal"
                                onChange={e => this.setState({ VALOR_RAMAL: e.target.value })}
                                required
                            />
                            <a>Batch</a>
                            <input
                                type="number"
                                placeholder="Batch"
                                maxlength="3"
                                onChange={e => this.setState({ BATCH_ID: e.target.value })}
                                required
                            />
                            <button type="submit">Registrar</button>
                        </Form>

                        <div className="groups">
                            <div className="table">
                                <table>
                                    <thead>
                                        <tr>
                                            <th>Ramal</th>
                                            <th>Grupo</th>
                                            <th>Tipo de Linha</th>
                                            <th>Data de Cadastro</th>
                                            <th>Status</th>
                                        </tr>
                                    </thead>
                                    {info.map(data => (
                                        <tbody key={data.linha}>
                                            <tr>
                                                <td>{data.linha}</td>
                                                <td>{data.Grupo}</td>
                                                <td>{data.tipo_linha}</td>
                                                <td>{data.data_envio_nova}</td>
                                                <td>{data.status}</td>
                                            </tr>
                                        </tbody>
                                    ))}
                                </table>

                            </div>

                        </div>
                    </Container>
                </div>
            </div>

        )
    }
}

export default withRouter(keepFares);
