import React, { Component } from "react";
import Menu from "../../menu/menu";
import Select from 'react-select';
import api from "../../../services/api";
import Groups from "../../../utils/groups.json";
import { Form, Container } from "./styles";

export default class activateExts extends Component {
    constructor() {
        super();
        this.state = {
            page_num: 1,
            extSelected: null,
            extGroupSelected: null,
            ext: null,
            group: null,
            info: [],
        }
    }

    componentDidMount = () => {
        this.pendentExt();
        this.pendentGroup();
    }


    // Retorna os ramais pendentes para ativação
    pendentExt = async () => {
        const { page_num } = this.state;
        let exts = [];

        const response = await api.get(`/extensions/${page_num}/P`);
        const { docs } = response.data;
        exts = docs.map((dt) => { return { value: dt.linha, label: dt.linha } })

        // console.log(selectedGroup);
        this.setState({ info: docs, ext: exts });
        // this.extGroupChange(selectedGroup)
    }

    pendentGroup = () => {
        let groups = [];
        groups = Groups.groups.map((dt) => { return { value: dt.value, label: dt.label } })
        this.setState({ group: groups })
    }

    extChange = async extSelected => {
        const { page_num } = this.state;
        let selectedGroup;
        const response = await api.get(`/extensions/${page_num}/P`);
        const { docs } = response.data;
        selectedGroup = docs.map((dt) => { return { value: dt.user_group_id, label: dt.Grupo, ext: dt.linha } }).find((sg) => sg.ext === extSelected.value)
        // const select = selectedGroup.find((sg) => sg.ext === extSelected.value)
        this.setState({ extSelected, extGroupSelected: selectedGroup });
        console.log(`Ext Selected: `, extSelected.value);
    };

    extGroupChange = extGroupSelected => {
        this.setState({ extGroupSelected });
        console.log(`Grupo Selected: `, extGroupSelected);
    }

    handleOptionChange = (changeEvent) => {
        this.setState({ selectedOption: changeEvent.target.value });
        console.log("selectedOption", this.state.selectedOption);
    }

    render() {
        const { extSelected, ext, extGroupSelected, group, selectedOption } = this.state;
        return (
            <div className="mainDiv">
                <div className="menu">
                    <Menu />
                </div>

                <div className="formExt">
                    <p>Ativar Ramais</p>
                    <Container>
                        <Form onSubmit={this.handleSubmit}>
                            <Select
                                className="selected"
                                value={extSelected}
                                onChange={this.extChange}
                                options={ext}
                                maxMenuHeight={150}
                                placeholder="Selecione o Ramal"
                                isSearchable />
                            <Select
                                className="selected"
                                value={extGroupSelected}
                                onChange={this.extGroupChange}
                                options={group}
                                maxMenuHeight={150}
                                placeholder="Selecione o Grupo"
                                isSearchable />

                            <div className="radio-toolbar">
                                <input
                                    type="radio"
                                    value="activateExt"
                                    checked={selectedOption === "activateExt"}
                                    onChange={this.handleOptionChange}
                                />
                                <label for="activateExt">Ativar Ramal</label>
                                <input
                                    type="radio"
                                    value="changeExt"
                                    checked={selectedOption === "changeExt"}
                                    onChange={this.handleOptionChange}
                                />
                                <label for="changeExt">Mudar Ramal de Grupo</label>
                                <input
                                    type="radio"
                                    value="deactivateExt"
                                    checked={selectedOption === "deactivateExt"}
                                    onChange={this.handleOptionChange}
                                />
                                <label for="deactivateExt">Desativar</label>
                            </div>
                            <button className="doBackup" onClick={this.handleSubmit}>Executar</button>
                        </Form>
                    </Container>
                </div>
            </div >

        )
    }
}
